//Navbar code and links//

import { useState, useEffect } from "react";
import { Navbar, Container, Nav } from "react-bootstrap";
import ialogo from '../img/ialogo.svg';


export const NavBar = () => {
  const [activeLink, setActiveLink] = useState('home');
  const [scrolled, setScrolled] =useState(false);

  useEffect(() => {
    const onScroll = () => {
      if (window.scrollY > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    }
    window.addEventListener("scroll", onScroll);

    return () => window.removeEventListener("scroll", onScroll);
  },[])

  const onUpdatedActiveLink = (value) => {
    setActiveLink(value);
  }

  return (
    <Navbar expand="lg" className={scrolled ? "scrolled": ""}>
    <Container>
      <Navbar.Brand href="#home">
        <img src={ialogo} alt="Logo"/>
        </Navbar.Brand>
      <Navbar.Toggle aria-controls="basic-navbar-nav">
        <span className="navbar-toggler-icon"></span>
        </Navbar.Toggle>
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="me-auto">
          <Nav.Link href="#home" className={activeLink === 'home' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdatedActiveLink('home')}>Home</Nav.Link>
          <Nav.Link href="#about"className={activeLink === 'about' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdatedActiveLink('about')}>About Me</Nav.Link>
          <Nav.Link href="#projects"className={activeLink === 'projects' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdatedActiveLink('projects')}>Projects</Nav.Link>
          <Nav.Link href="#skills"className={activeLink === 'skills' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdatedActiveLink('skills')}>Skills</Nav.Link>
          <Nav.Link href="#links"className={activeLink === 'links' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdatedActiveLink('links')}>Links</Nav.Link>
        </Nav>
        {/* <span className="navbar-text">
          <div className="social-icon">
            <a href="#"><img src={} alt="" /></a>
            <a href="#"><img src={} alt="" /></a>
            <a href="#"><img src={} alt="" /></a>
            <a href="#"><img src={} alt="" /></a>
            <a href="#"><img src={} alt="" /></a>
          </div>
          <button className="vvd" onClick={() => console.log('connect')}><span> Let's Connect</span></button>
        </span> */}
      </Navbar.Collapse>
    </Container>
  </Navbar>

  )
}
